SELECT [eventID] = N'INBO:MEETNET:EVENT:' + Right( N'000000000' + CONVERT(nvarchar(20) , fA.FieldworkSampleID),6)  	, geometry::EnvelopeAggregate(dl.LocationGeom) as envloppe	, geometry::CollectionAggregate(dl.LocationGeom) as Collec	, geometry::ConvexHullAggregate(dl.LocationGeom) as Convex	, geometry::UnionAggregate(dl.LocationGeom) as unie	, MAX( fa.LocationID ) as LocationID	, CASE WHEN Count(*) >1  THEN 'Lijst alle unieke referenties naar locatie (' + dbo.concatenate( 1, CONVERT(Nvarchar(200), fa.LocationID ) , ', ') 		ELSE ' niets te melden' 		END as VerbatimLocation	--, dl.LocationGeom	--, dl.parentLocationGeom	, fa.ProjectKey	--, fa.LocationID	, COUNT(*) as nbrFROM (SELECT DISTINCT(FieldworkSampleID),FieldworkVisitID,ProjectKey, LocationKey, ProtocolKey, LocationID, ProtocolID, SpeciesActivityID, SpeciesActivityKey, SpeciesLifestageID, SpeciesLifestageKey FROM dbo.FactAantal WHERE FieldworkSampleID > 0) fA	INNER JOIN dbo.dimProject dP ON dP.ProjectKey = fA.ProjectKey	INNER JOIN dbo.DimLocation dL ON dL.LocationKey = fA.LocationKey	INNER JOIN dbo.DimProtocol dProt ON dProt.ProtocolKey = fA.ProtocolKey	INNER JOIN dbo.DimSpeciesActivity dSA ON dSA.SpeciesActivityKey = fA.SpeciesActivityKey	INNER JOIN dbo.DimSpeciesLifestage dSL ON dSL.SpeciesLifestageKey = fA.SpeciesLifestageKey	--	INNER JOIN FactCovariabele FCo ON FCo.FieldworkSampleID = fA.FieldworkSampleID	INNER JOIN (SELECT DISTINCT(FieldworkSampleID), VisitStartDate FROM dbo.FactWerkpakket ) FWp ON FWp.FieldworkSampleID = fa.FieldworkSampleID	--INNER JOIN FactCovariabele FCo ON FCo.FieldworkSampleID = fA.FieldworkSampleIDWHERE 1=1--AND ProjectName = 'Vuursalamander'--AND ProtocolName = 'Vlinders - Transecten'--AND fa.ProjectKey = '16'  AND fa.ProtocolID =  '1'    AND fa.FieldworkSampleID in (54759, 196710)--  AND fa.FieldworkSampleID in ('196717','196456','197026','54759','194584')  --AND ParentLocationGeom IS NULL--ORDER BY FA.FieldworkSampleID DesC--- Verification by counts --- GROUP BY N'INBO:MEETNET:EVENT:' + Right( N'000000000' + CONVERT(nvarchar(20) , fA.FieldworkSampleID),6)  	, fa.ProjectKey